let likes=$(".like-link");for(i of likes)i.addEventListener("click",e=>{handleLike(e)});function handleLike(e){e.preventDefault(),$.ajax({type:"get",url:e.target.href,success:function(l){let t=e.target.href.split("=").pop().toLowerCase(),a=e.target.href.split("=")[1];a=a.split("&")[0],l.data.deleted?($(`#like-${t}-${a} p`).html(l.data.length),$(`#like-${t}-${a} a i`).toggleClass("fa-solid fa-regular"),$(`#like-${t}-${a} a i`).css("color","#000000")):($(`#like-${t}-${a} p`).html(l.data.length),$(`#like-${t}-${a} a i`).toggleClass("fa-regular fa-solid"),$(`#like-${t}-${a} a i`).css("color","#ff0000"))}})}